import { listenTo, createRef } from './helpers'

export default function createOnChildAdded(context) {
  return (path, signal, options = {}) => {
    listenTo(createRef(path, options), path, 'child_added', signal, data => {
      context.controller.getSignal(signal)(
        Object.assign(
          {
            key: data.key,
            value: data.val(),
          },
          options.payload || {}
        )
      )
    })
  }
}
