export default class CerebralError extends Error {
  constructor(message, details) {
    super(message)
    this.name = 'CerebralError'
    this.details = details
    this.toJSON = function() {
      return Object.assign(
        {
          message: this.message,
          stack: this.stack,
        },
        Object.keys(this).reduce((props, key) => {
          props[key] = this[key]

          return props
        }, {})
      )
    }
  }
}
