/* eslint-env mocha */
import App, { Provider } from '../'

import assert from 'assert'

describe('Debugger', () => {
  it('should send debuggerData', () => {
    const rootModule = {
      sequences: {
        foo: [
          (context) => {
            context.debugger.send(1)
          },
        ],
      },
    }
    const controller = App(rootModule, {
      devtools: {
        init() {},
        send() {},
        sendExecutionData(debuggerData) {
          assert.strictEqual(debuggerData, 1)
        },
      },
    })
    controller.getSequence('foo')()
  })
  it('should wrap provider', () => {
    const rootModule = {
      providers: {
        myProvider: Provider({ doSomething() {} }),
      },
      sequences: {
        foo: [
          ({ myProvider }) => {
            myProvider.doSomething(1)
          },
        ],
      },
    }
    const controller = App(rootModule, {
      devtools: {
        init() {},
        send() {},
        sendExecutionData(debuggerData) {
          assert.strictEqual(debuggerData.type, 'provider')
          assert.strictEqual(debuggerData.method, 'myProvider.doSomething')
          assert.strictDeepEqual(debuggerData.args, [1])
          assert.strictEqual(debuggerData.returnValue, undefined)
          assert.ok(debuggerData.datetime)
        },
      },
    })
    controller.getSequence('foo')()
  })
  it('throw send debuggerData', () => {
    const rootModule = {
      sequences: {
        foo: [() => {}],
      },
    }
    const controller = App(rootModule, {
      devtools: {
        init() {},
        send() {},
        sendExecutionData() {},
        preventPropsReplacement: true,
      },
    })
    controller.getSequence('foo')()
  })
  it('should be able to call wrapped providers directly', () => {
    let callCount = 0
    const foo = {
      providers: {
        providerA: Provider({ foo() {} }),
      },
    }
    const bar = {
      providers: {
        providerB: Provider({
          foo() {
            return this.context.providerA.foo()
          },
        }),
      },
    }
    const rootModule = {
      modules: {
        foo,
        bar,
      },
      sequences: {
        foo: [
          ({ providerB }) => {
            providerB.foo()
          },
        ],
      },
    }
    const controller = App(rootModule, {
      devtools: {
        init() {},
        sendExecutionData() {
          callCount++
        },
      },
    })
    controller.getSequence('foo')()
    assert.strictEqual(callCount, 1)
  })
})
