/* eslint-env mocha */
import Path from './Path'
import assert from 'assert'

describe('primitives', () => {
  it('should initiate correctly', () => {
    const payload = {
      foo: 'bar',
    }
    const path = new Path('path', payload)

    assert.ok(path instanceof Path)
    assert.strictEqual(path.path, 'path')
    assert.strictDeepEqual(path.payload, payload)
    assert.strictEqual(path.toJSON().path, 'path')
    assert.strictDeepEqual(path.toJSON().payload, payload)
  })
})
